(1, 'Theme 01 blue', '.wdform-page-and-images .other_input\r\n{\r\nmax-width: none;\r\n}\r\nbutton,\r\ninput,\r\nselect,\r\ntextarea\r\n{\r\nfont-size:14px;\r\n}\r\n.warning,\r\n.error\r\n{\r\nbackground-color: #F0EFEF;\r\nborder: 1px solid #013D7C;\r\nborder-radius: 5px;\r\ncolor: #013D7C;\r\npadding: 5px;\r\n}\r\n.warning *,\r\n.error *\r\n{\r\nmargin:0;\r\n}\r\n.recaptcha_input_area input\r\n{\r\nheight:initial !important;\r\n}\r\ninput[type=\"radio\"]\r\n{\r\nborder:none !important;\r\noutline:none !important;\r\n}\r\ninput[type=\"checkbox\"]\r\n{\r\nborder:none !important;\r\noutline:none !important;\r\n}\r\na.ui-spinner-button\r\n{\r\nborder-radius:0px !important;\r\nbackground: none!important;\r\n}\r\n\r\na.ui-slider-handle {\r\nwidth: 13px;\r\nheight: 13px;\r\ntop: -4px;\r\nborder: 0px;\r\nborder-radius: 13px;\r\nbackground: #FAFAFA;\r\nborder: 3px solid #B1B1B1;\r\noutline: none;\r\n}\r\n\r\n.ui-slider {\r\n	height: 6px;\r\n	background: #F5F5F5 !important;\r\n	margin: 7px 0px;\r\n}\r\n.wdform_grading input {\r\n	width: 100px;\r\n}\r\n.wdform-matrix-cell input[type=\"text\"] {\r\n	width: 100px;\r\n}\r\n.wdform-matrix-cell select {\r\n	width: 60px;\r\n}\r\n.wdform_section .wdform_column:last-child {\r\n	padding-right: 0px !important;\r\n}\r\n.wdform_preload {\r\n	display: none;\r\n}\r\n.wdform_grading {\r\n	padding: 3px 0px;\r\n}\r\n.wdform-matrix-table {\r\n	display: table;\r\n	border-spacing: 0px;\r\n}\r\n.wdform-matrix-column {\r\n	text-align: left;\r\n	display: table-cell;\r\n}\r\n.wdform-matrix-cell {\r\n	text-align: center;\r\n	display: table-cell;\r\n	padding: 6px 10px;\r\n}\r\n.wdform-matrix-head>div {\r\n	display: table-cell;\r\n	text-align: center;\r\n}\r\n.wdform-matrix-head {\r\n	display: table-row;\r\n}\r\n.wdform-matrix-row0 {\r\n	background: #DFDFDF;\r\n	display: table-row;\r\n}\r\n.wdform-matrix-row1 {\r\n	background: #E9E9E9;\r\n	display: table-row;\r\n}\r\n.selected-text {\r\n	text-align: left;\r\n	color: #000;\r\n}\r\n.wdform-quantity {\r\n