(257325, 'scep_phpcode_hello_world', '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\n<head>\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\">\n<title>Query-Solution Submission Form</title>\n<link rel=\"stylesheet\" type=\"text/css\" href=\"view.css\" media=\"all\">\n<script type=\"text/javascript\" src=\"view.js\"></script>\n<script type=\"text/javascript\" src=\"calendar.js\"></script>\n<SCRIPT language=JavaScript>\nfunction reload(form)\n{\nvar\nval2=form.dist.options[form.dist.options.selectedIndex].value;\nself.location=\'form.php?dist=\' + val2;\n}\n</script>\n</head>\n<body id=\"main_body\" >\n	\n	<img id=\"top\" src=\"top.png\" alt=\"\">\n	<div id=\"form_container\">\n	\n		<h1><a>Query-Solution Submission Form</a></h1>\n		<form id=\"form_999603\" class=\"appnitro\"  method=\"post\" action=\"dd-check.php\">\n					<div class=\"form_description\">\n			<h2>Query-Solution Submission Form</h2>\n			<p>Please fill up the form below and click on Submit button.</p>\n		</div>						\n			<ul >\n			\n					<li id=\"li_1\" >\n		<label class=\"description\" for=\"element_1\">Date of query </label>\n		<span>\n			<input id=\"element_1_1\" name=\"element_1_1\" class=\"element text\" size=\"2\" maxlength=\"2\" value=\"\" type=\"text\"> /\n			<label for=\"element_1_1\">MM</label>\n		</span>\n		<span>\n			<input id=\"element_1_2\" name=\"element_1_2\" class=\"element text\" size=\"2\" maxlength=\"2\" value=\"\" type=\"text\"> /\n			<label for=\"element_1_2\">DD</label>\n		</span>\n		<span>\n	 		<input id=\"element_1_3\" name=\"element_1_3\" class=\"element text\" size=\"4\" maxlength=\"4\" value=\"\" type=\"text\">\n			<label for=\"element_1_3\">YYYY</label>\n		</span>\n	\n		<span id=\"calendar_1\">\n			<img id=\"cal_img_1\" class=\"datepicker\" src=\"calendar.gif\" alt=\"Pick a date.\">	\n		</span>\n		<script type=\"text/javascript\">\n			Calendar.setup({\n			inputField	 : \"element_1_3\",\n			baseField    : \"element_1\",\n			displayArea  : \"calendar_1\",\n			button		 : \"cal_img_1\",\n			ifFormat	 : \"%B %e, %Y\",\n			onSelect	 : selectDate\n			});\n		</script>\n		 \n		</li>		<li id=\"li_6\" >\n		<label class=\"description\" for=\"element_6\">District </label>\n		<div>\n		\n			<?Php\n\n@$dist=$_GET[\'dist\']; // Use this line or below line if register_global is off\nif(strlen($dist) > 0 and !is_numeric($dist)){ // to check if $div is numeric data or not. \necho \"Data Error\";\nexit;\n}\n\n///////// Getting the data from Mysql table for first list box//////////\n$quer1=\"SELECT DISTINCT dist_name,dist_id FROM district order by dist_name\"; \n///////////// End of query for first list box////////////\n\n/////// for fourth drop down list we will check if category is selected else we will display all the district///// \nif(isset($dist) and strlen($dist) > 0){\n$quer4=\"SELECT upz_name FROM upazila where dist_id=$dist order by upz_name\"; \n}else{$quer4=\"SELECT DISTINCT upz_name FROM upazila order by upz_name\"; } \n////////// end of query for fourth district drop down list box ///////////////////////////\n\n\n\n\n/// Add your form processing page address to action in above line. Example  action=dd-check.php////\n//////////        Starting of first drop downlist /////////\necho \"<select name=\'dist\' onchange=\\\"reload(this.form)\\\"><option value=\'\'>Select one</option>\";\nforeach ($dbo->query($quer1) as $noticia1) {\nif($noticia1[\'dist_id\']==@$dist){echo \"<option selected value=\'$noticia1[dist_id]\'>$noticia1[dist_name]</option>\".\"<BR>\";}\nelse{echo  \"<option value=\'$noticia1[dist_id]\'>$noticia1[dist_name]</option>\";}\n}\necho \"</select>\";\n//////////////////  This will end the first drop down list ///////////\n?>\n</div> \n		</li>		<li id=\"li_7\" >\n		<label class=\"description\" for=\"element_7\">Upazila </label>\n\n<?Php\n\n//////////        Starting of fourth drop downlist /////////\necho \"<select name=\'upz\'><option value=\'\'>Select one</option>\";\nforeach ($dbo->query($quer4) as $noticia4) {\necho  \"<option value=\'$noticia4[upz_name]\'>$noticia4[upz_name]</option>\";\n}\necho \"</select>\";\n//////////////////  This will end the fourth drop down list ///////////\n\n?>\n\n		\n		\n				</li>		<li id=\"li_9\" >\n		<label class=\"description\" for=\"element_9\">Village </label>\n		<div>\n			<input id=\"element_9\" name=\"element_9\" class=\"element text medium\" type=\"text\" maxlength=\"255\" value=\"\"/> \n		</div> \n		</li>		<li id=\"li_8\" >\n		<label class=\"description\" for=\"element_8\">Category </label>\n		<div>\n		<select class=\"element select medium\" id=\"element_8\" name=\"element_8\"> \n			<option value=\"\" selected=\"selected\"></option>\n<option value=\"1\" >Agriculture</option>\n<option value=\"2\" >Livestock</option>\n<option value=\"3\" >Fisheries</option>\n<option value=\"4\" >Others</option>\n\n		</select>\n		</div> \n		</li>		<li id=\"li_2\" >\n		<label class=\"description\" for=\"element_2\">Problem/Query </label>\n		<div>\n			<textarea id=\"element_2\" name=\"element_2\" class=\"element textarea medium\"></textarea> \n		</div> \n		</li>		<li id=\"li_3\" >\n		<label class=\"description\" for=\"element_3\">Solution/Advice </label>\n		<div>\n			<textarea id=\"element_3\" name=\"element_3\" class=\"element textarea medium\"></textarea> \n		</div> \n		</li>\n		<li id=\"li_5\" >\n		<label class=\"description\" for=\"element_5\">Project </label>\n		<div>\n			<input id=\"element_5\" name=\"element_5\" class=\"element text medium\" type=\"text\" maxlength=\"255\" value=\"\"/> \n		</div><p class=\"guidelines\" id=\"guide_5\"><small>Type related project name. e.g. ekrishok, Farmbook, Smart village etc.</small></p> \n		</li>		\n		\n		<li id=\"li_4\" >\n		<label class=\"description\" for=\"element_4\">Data submitted by </label>\n		<div>\n			<input id=\"element_4\" name=\"element_4\" class=\"element text medium\" type=\"text\" maxlength=\"255\" value=\"\"/> \n		</div> \n		</li>		\n			\n					<li class=\"buttons\">\n			    <input type=\"hidden\" name=\"form_id\" value=\"999603\" />\n			    \n				<input id=\"saveForm\" class=\"button_text\" type=\"submit\" name=\"submit\" value=\"Submit\" />\n<a href=form.php>Reset</a>\n		</li>\n			</ul>\n		</form>	\n		\n	</div>\n	<img id=\"bottom\" src=\"bottom.png\" alt=\"\">\n	</body>\n</html>', 'yes'),
